mapper.select:
SELECT * FROM ( SELECT ROWNUM rno, route.* FROM (
SELECT DISTINCT(route_id), route_nm, r.route_cd route_cd, route_tp, st_sta_nm, ed_sta_nm, region FROM route r, district d ,routetype t,( select st_sta_id , station_nm st_sta_nm from station, route where station_id = st_sta_id ) st,( select ed_sta_id , station_nm ed_sta_nm from station, route where station_id = ed_sta_id )ed WHERE r.ed_sta_id = ed.ed_sta_id and d.district_cd = r.district_cd AND r.st_sta_id = st.st_sta_id and r.route_cd = t.route_cd and route_nm like #{keyword} || '%'
UNION
SELECT DISTINCT(route_id), route_nm, s.route_cd AS route_cd, route_tp, st_sta_nm, ed_sta_nm, region FROM seoulroute s, routetype t, district d WHERE s.route_cd = t.route_cd AND s.district_cd = d.district_cd AND route_nm LIKE #{keyword} || '%' ORDER BY route_nm
) route ) WHERE rno BETWEEN #{page.startCont} AND #{page.endCont}

SELECT rno, station_id, station_nm, mobile_no, region FROM ( SELECT ROWNUM rno, station_id, station_nm, REPLACE(mobile_no, ' ', '고유번호없음') mobile_no, region FROM ( SELECT station_id, station_nm, mobile_no, region FROM station s, district d WHERE station_nm like #{keyword} || '%' AND s.district_cd = d.district_cd AND s.district_cd = 2
UNION
SELECT station_id, station_nm, mobile_no, region FROM seoulstation s, district d WHERE station_nm like #{keyword} || '%' AND s.district_cd = d.district_cd ORDER BY station_nm ) ) WHERE rno BETWEEN #{page.startCont} AND #{page.endCont}

SELECT bmno, route_id, station_id FROM bookmark
WHERE isshow = 'Y' AND mno = ( SELECT mno FROM member WHERE id = #{id} ) AND route_id IS NOT NULL AND station_id IS NOT NULL AND route_id = #{route_id}
WHERE isshow = 'Y' AND mno = ( SELECT mno FROM member WHERE id = #{id} ) AND route_id IS NOT NULL AND station_id IS NOT NULL AND station_id = #{station_id}
